            color: #1e293b;
        }

        .close-btn {
            width: 36px;
            height: 36px;
            border: none;
            background: #f1f5f9;
            border-radius: 8px;
            cursor: pointer;
            font-size: 20px;
            color: #64748b;
            transition: all 0.2s;
        }

        .close-btn:hover {
            background: #e2e8f0;
            transform: rotate(90deg);
        }

        .preview-body {
            padding: 32px;
            max-height: calc(90vh - 200px);
            overflow-y: auto;
        }

        .demo-grid {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: 12px;
        }

        .demo-slot {
            background: #f1f5f9;
            border-radius: 12px;
            height: 120px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-weight: 700;
            color: #94a3b8;
            font-size: 14px;
            background-size: cover;
            background-position: center;
            position: relative;
            overflow: hidden;
        }

        .demo-slot.large {
            grid-column: span 2;
            height: 180px;
        }

        .demo-text-overlay {
            position: absolute;
            bottom: 16px;
            left: 16px;
            right: 16px;
            background: rgba(255, 255, 255, 0.95);
            backdrop-filter: blur(10px);
            padding: 12px;
            border-radius: 8px;
        }

        .demo-title {
            font-size: 13px;
            font-weight: 700;
            color: #1e293b;
            margin-bottom: 4px;
        }

        .demo-subtitle {
            font-size: 11px;
            color: #64748b;
        }

        .preview-footer {
            padding: 20px 32px;
            border-top: 1px solid #e2e8f0;
            background: #f8fafc;
            display: flex;
            justify-content: flex-end;
            gap: 12px;
        }

        /* Responsive */
        @media (max-width: 768px) {
            .page-header {
                flex-direction: column;
                align-items: flex-start;
                gap: 20px;
            }

            .stats-bar {
                width: 100%;
                justify-content: space-between;
            }

            .header-actions {
                width: 100%;
            }

            .btn {
                flex: 1;
                justify-content: center;
            }

            .banners-grid {
                grid-template-columns: 1fr;
            }

            .demo-grid {
                grid-template-columns: repeat(2, 1fr);
            }

            .demo-slot.large {
                grid-column: span 2;
            }
        }

        /* Loading Animation */
        .skeleton {
            background: linear-gradient(90deg, #f1f5f9 25%, #e2e8f0 50%, #f1f5f9 75%);
            background-size: 200% 100%;
            animation: loading 1.5s infinite;
        }

        @keyframes loading {
            0% { background-position: 200% 0; }
            100% { background-position: -200% 0; }
        }

        /* Tooltip */
        [data-tooltip] {
            position: relative;
        }

        [data-tooltip]::after {
            content: attr(data-tooltip);
            position: absolute;
            bottom: 100%;
            left: 50%;
            transform: translateX(-50%);
            padding: 6px 12px;
            background: #1e293b;
            color: white;
            border-radius: 6px;
            font-size: 12px;
            white-space: nowrap;
            opacity: 0;
            pointer-events: none;
            transition: opacity 0.2s;
            margin-bottom: 8px;
        }

        [data-tooltip]:hover::after {
            opacity: 1;
        }
