args.content:
:root {
    --bg-color: #0a0a0a;
    --text-color: #ffffff;
    --accent-primary: #7928ca;
    --accent-secondary: #ff0080;
    --card-bg: #1a1a1a;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Inter', sans-serif;
    background-color: var(--bg-color);
    color: var(--text-color);
    line-height: 1.6;
    overflow-x: hidden;
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 50px;
    backdrop-filter: blur(10px);
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 100;
}

.logo {
    font-weight: 800;
    font-size: 1.5rem;
    letter-spacing: -1px;
}

.badge {
    background: linear-gradient(45deg, var(--accent-primary), var(--accent-secondary));
    padding: 2px 6px;
    border-radius: 4px;
    font-size: 0.8rem;
    vertical-align: middle;
}

nav a {
    color: #ccc;
    text-decoration: none;
    margin-right: 20px;
    font-size: 0.9rem;
    transition: color 0.3s;
}

nav a:hover {
    color: white;
}

.btn-secondary {
    background: transparent;
    border: 1px solid #333;
    color: white;
    padding: 8px 16px;
    border-radius: 6px;
    cursor: pointer;
}

.hero {
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 10%;
    background: radial-gradient(circle at 80% 50%, #2a1b3d 0%, #0a0a0a 60%);
}

.hero-content {
    max-width: 600px;
}

h1 {
    font-size: 4rem;
    line-height: 1.1;
    margin-bottom: 20px;
    font-weight: 800;
}

.gradient-text {
    background: linear-gradient(90deg, var(--accent-secondary), var(--accent-primary));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.hero p {
    font-size: 1.2rem;
    color: #aaa;
    margin-bottom: 40px;
}

.cta-group {
    display: flex;
    gap: 20px;
    align-items: center;
}

.btn-primary {
    background: white;
    color: black;
    border: none;
    padding: 15px 30px;
    font-size: 1rem;
    font-weight: 600;
    border-radius: 30px;
    cursor: pointer;
    transition: transform 0.2s;
}

.btn-primary:hover {
    transform: scale(1.05);
}

.upload-area {
    border: 2px dashed #444;
    padding: 12px 25px;
    border-radius: 12px;
    color: #888;
    display: flex;
    align-items: center;
    gap: 10px;
    cursor: pointer;
}

.upload-area:hover {
    border-color: var(--accent-primary);
    color: var(--accent-primary);
}

/* Visual Waveform Animation */
.hero-visual {
    position: relative;
    width: 300px;
    height: 200px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.waveform {
    display: flex;
    align-items: center;
    gap: 5px;
}

.bar {
    width: 10px;
    background: linear-gradient(to top, var(--accent-primary), var(--accent-secondary));
    border-radius: 5px;
    animation: wave 1s infinite ease-in-out;
}

@keyframes wave {
    0%, 100% { height: 20px; }
    50% { height: 150px; }
}

.bar:nth-child(odd) { animation-duration: 0.8s; }
.bar:nth-child(even) { animation-duration: 1.2s; }

.features {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 30px;
    padding: 100px 10%;
}

.card {
    background: var(--card-bg);
    padding: 30px;
    border-radius: 16px;
    border: 1px solid #333;
}

.card h3 {
    margin-bottom: 15px;
    color: var(--accent-secondary);
}

footer {
    text-align: center;
    padding: 50px;
    color: #666;
    border-top: 1px solid #222;
}

