const puppeteer = require('puppeteer');
const path = require('path');

(async () => {
  console.log('–ó–∞–ø—É—Å–∫ –ø—Ä–æ–≤–µ—Ä–∫–∏ localhost:3000...');
  const browser = await puppeteer.launch({
    headless: true,
    args: ['--no-sandbox', '--disable-setuid-sandbox']
  });
  const page = await browser.newPage();

  // –ü—Ä–æ–±—Ä–æ—Å –∫–æ–Ω—Å–æ–ª–∏
  page.on('console', msg => {
    const type = msg.type().toUpperCase();
    console.log(`[BROWSER ${type}]: ${msg.text()}`);
  });

  // –û—Ç–ª–æ–≤ –æ—à–∏–±–æ–∫ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
  page.on('pageerror', err => {
    console.error(`[BROWSER ERROR]: ${err.message}`);
  });

  page.on('requestfailed', request => {
    console.error(`[NETWORK FAIL]: ${request.url()} - ${request.failure().errorText}`);
  });

  try {
    const response = await page.goto('http://localhost:3000', { waitUntil: 'networkidle0' });

    if (response.status() === 200) {
        console.log('–£—Å–ø–µ—à–Ω–æ–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –∫ http://localhost:3000');
    } else {
        console.error(`–û—à–∏–±–∫–∞ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è: –°—Ç–∞—Ç—É—Å ${response.status()}`);
    }

    // –°–∫—Ä–∏–Ω—à–æ—Ç
    const screenshotPath = path.resolve(__dirname, 'server-screenshot.png');
    await page.screenshot({ path: screenshotPath, fullPage: true });
    console.log(`–°–∫—Ä–∏–Ω—à–æ—Ç —Å–æ—Ö—Ä–∞–Ω–µ–Ω: ${screenshotPath}`);

  } catch (error) {
    console.error('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å—Ç—Ä–∞–Ω–∏—Ü—É:', error);
  }

  await browser.close();

  // We don't want to kill the server here, assuming the user wants it running.
  // But for this CLI session, I'll exit the check script.
})();

